function Get-TranscriptFiles {
    param(
        [string[]]$SearchPaths = @(
            "C:\",
            "$env:USERPROFILE\Documents",
            "$env:USERPROFILE\Desktop",
            "C:\Windows\System32\LogFiles",
            "C:\Transcripts"
        ),
        [int]$LastDays = 30
    )

    $startDate = (Get-Date).AddDays(-$LastDays)

    Write-Host "`n=== PowerShell Transcript Files Search ===" -ForegroundColor Cyan

    foreach ($path in $SearchPaths) {
        if (Test-Path $path) {
            Write-Host "`nSearching in: $path" -ForegroundColor Yellow

            # Look for transcript files
            $transcripts = Get-ChildItem -Path $path -Include "*transcript*.txt", "*PowerShell_transcript*", "*.log" -Recurse -ErrorAction SilentlyContinue |
                          Where-Object { $_.LastWriteTime -gt $startDate }

            if ($transcripts) {
                Write-Host "[+] Found $($transcripts.Count) transcript files" -ForegroundColor Green

                foreach ($file in $transcripts | Sort-Object LastWriteTime -Descending | Select-Object -First 5) {
                    Write-Host "`nFile: $($file.FullName)" -ForegroundColor Cyan
                    Write-Host "Size: $([math]::Round($file.Length/1KB,2)) KB" -ForegroundColor Gray
                    Write-Host "Modified: $($file.LastWriteTime)" -ForegroundColor Gray

                    # Check first few lines for PowerShell transcript header
                    $header = Get-Content $file.FullName -First 5 -ErrorAction SilentlyContinue
                    if ($header -match "PowerShell transcript start|Start time") {
                        Write-Host "Confirmed PowerShell Transcript" -ForegroundColor Green

                        # Look for suspicious content
                        $content = Get-Content $file.FullName -ErrorAction SilentlyContinue | Select-String -Pattern "IEX|DownloadString|bypass|amsi|mimikatz"
                        if ($content) {
                            Write-Host "[!] Suspicious content found!" -ForegroundColor Red
                            $content | Select-Object -First 3 | ForEach-Object {
                                Write-Host "  $($_.Line)" -ForegroundColor Magenta
                            }
                        }
                    }
                }
            } else {
                Write-Host "[-] No transcript files found" -ForegroundColor Red
            }
        }
    }

    # Check registry for transcription settings
    Write-Host "`n=== Transcription Registry Settings ===" -ForegroundColor Cyan
    $transcriptionKey = "HKLM:\SOFTWARE\Policies\Microsoft\Windows\PowerShell\Transcription"

    if (Test-Path $transcriptionKey) {
        $settings = Get-ItemProperty -Path $transcriptionKey -ErrorAction SilentlyContinue
        if ($settings) {
            Write-Host "[!] Transcription is configured in registry" -ForegroundColor Red
            $settings.PSObject.Properties | Where-Object { $_.Name -notlike "PS*" } | ForEach-Object {
                Write-Host "  $($_.Name): $($_.Value)" -ForegroundColor Yellow
            }

            # Check output directory
            if ($settings.OutputDirectory) {
                if (Test-Path $settings.OutputDirectory) {
                    Write-Host "`nChecking transcription output directory: $($settings.OutputDirectory)" -ForegroundColor Yellow
                    $transcriptFiles = Get-ChildItem -Path $settings.OutputDirectory -Include "*.txt", "*.log" -ErrorAction SilentlyContinue
                    if ($transcriptFiles) {
                        Write-Host "[+] Found $($transcriptFiles.Count) files in transcription directory" -ForegroundColor Green
                    }
                }
            }
        }
    } else {
        Write-Host "[+] No transcription registry settings found" -ForegroundColor Green
    }
}

