    foreach ($file in $psFiles) {
        # Check for ADS
        $streams = Get-Item -Path $file.FullName -Stream * -ErrorAction SilentlyContinue

        if ($streams.Count -gt 1) {  # More than just :$DATA
            Write-Host "`n[!] File with ADS found: $($file.FullName)" -ForegroundColor Red

            foreach ($stream in $streams) {
                if ($stream.Stream -ne ':$DATA') {
                    Write-Host "  Stream: $($stream.Stream)" -ForegroundColor Yellow
                    Write-Host "  Length: $($stream.Length) bytes" -ForegroundColor Gray

                    # Try to read the stream content
                    try {
                        $streamPath = "$($file.FullName):$($stream.Stream)"
                        $content = Get-Content -Path $streamPath -Raw -ErrorAction Stop

                        if ($content.Length -lt 1000) {
                            Write-Host "  Content:" -ForegroundColor Cyan
                            Write-Host $content -ForegroundColor White
                        } else {
                            Write-Host "  Content (first 500 chars):" -ForegroundColor Cyan
                            Write-Host $content.Substring(0, 500) -ForegroundColor White
                        }
                    } catch {
                        Write-Host "  Could not read stream content" -ForegroundColor DarkGray
                    }
                }
            }
        }
    }
}

