```powershell
function Analyze-PowerShellFileMetadata {
    param(
        [string]$Path = "C:\Users"
    )

    Write-Host "`n=== PowerShell File Metadata Analysis ===" -ForegroundColor Cyan

    $psFiles = Get-ChildItem -Path $Path -Include *.ps1, *.psm1 -Recurse -ErrorAction SilentlyContinue |
               Where-Object { $_.Length -lt 5MB } |
               Select-Object -First 50

    Write-Host "Analyzing $($psFiles.Count) PowerShell files" -ForegroundColor Yellow

    $results = @()

    foreach ($file in $psFiles) {
        $metadata = [PSCustomObject]@{
            FileName = $file.Name
            FullPath = $file.FullName
            SizeKB = [math]::Round($file.Length/1KB, 2)
            Created = $file.CreationTime
            Modified = $file.LastWriteTime
            Accessed = $file.LastAccessTime
            Owner = (Get-Acl $file.FullName -ErrorAction SilentlyContinue).Owner
            DigitalSignature = $null
            CompanyName = $null
            FileVersion = $null
            Suspicious = $false
        }

        # Check for digital signature
        try {
            $sig = Get-AuthenticodeSignature -FilePath $file.FullName -ErrorAction Stop
            $metadata.DigitalSignature = $sig.Status
            if ($sig.Status -ne "Valid") {
                $metadata.Suspicious = $true
            }
        } catch {
            $metadata.DigitalSignature = "None/Error"
            $metadata.Suspicious = $true
        }

        # Check file content for suspicious patterns
        try {
            $content = Get-Content $file.FullName -First 20 -Raw -ErrorAction Stop
            $suspiciousPatterns = @(
                "IEX.*DownloadString",
                "New-Object.*Net.WebClient",
                "Start-Process.*-WindowStyle.*Hidden",
                "Add-MpPreference.*-ExclusionPath",
                "Set-MpPreference.*-DisableRealtimeMonitoring",
                "amsiInitFailed",
                "bypass.*-ExecutionPolicy"
            )

            foreach ($pattern in $suspiciousPatterns) {
                if ($content -match $pattern) {
                    $metadata.Suspicious = $true
                    break
                }
            }
        } catch {}

        $results += $metadata
    }

    # Display results
    Write-Host "`nSuspicious Files Found:" -ForegroundColor Red
    $results | Where-Object { $_.Suspicious -eq $true } | Select-Object FileName, FullPath, SizeKB, Modified, DigitalSignature | Format-Table -AutoSize

    Write-Host "`nAll Files (sorted by modified date):" -ForegroundColor Yellow
    $results | Sort-Object Modified -Descending | Select-Object FileName, FullPath, SizeKB, Modified, DigitalSignature, Suspicious | Format-Table -AutoSize
}

