function Get-PowerShellProcessInfo {
    Write-Host "`n=== PowerShell Process Analysis ===" -ForegroundColor Cyan

    # Find all PowerShell processes
    $psProcesses = Get-Process | Where-Object { 
        $_.ProcessName -match "powershell|pwsh" -or 
        $_.ProcessName -eq "PowerShell" 
    }

    Write-Host "Found $($psProcesses.Count) PowerShell processes" -ForegroundColor Yellow

    foreach ($proc in $psProcesses) {
        Write-Host "`nProcess: $($proc.ProcessName) (PID: $($proc.Id))" -ForegroundColor Green

        # Get process details
        try {
            # Get command line (requires admin)
            $wmiProc = Get-WmiObject Win32_Process -Filter "ProcessId = $($proc.Id)" -ErrorAction Stop
            if ($wmiProc.CommandLine) {
                Write-Host "Command Line: $($wmiProc.CommandLine)" -ForegroundColor Cyan

                # Check for suspicious command line arguments
                $suspiciousArgs = @(
                    "-NoProfile",
                    "-NonInteractive",
                    "-WindowStyle Hidden",
                    "-ExecutionPolicy Bypass",
                    "-EncodedCommand",
                    "-e ",
                    "-c ",
                    "IEX",
                    "DownloadString"
                )

                foreach ($arg in $suspiciousArgs) {
                    if ($wmiProc.CommandLine -match $arg) {
                        Write-Host "[!] Suspicious argument: $arg" -ForegroundColor Red
                    }
                }
            }

            # Get parent process
            $parentId = $wmiProc.ParentProcessId
            if ($parentId) {
                $parentProc = Get-Process -Id $parentId -ErrorAction SilentlyContinue
                if ($parentProc) {
                    Write-Host "Parent Process: $($parentProc.ProcessName) (PID: $parentId)" -ForegroundColor Gray
                }
            }

            # Get process owner
            $owner = $wmiProc.GetOwner()
            Write-Host "Owner: $($owner.Domain)\$($owner.User)" -ForegroundColor Gray

            # Get creation time
            Write-Host "Created: $([Management.ManagementDateTimeConverter]::ToDateTime($wmiProc.CreationDate))" -ForegroundColor Gray

            # Get memory usage
            Write-Host "Memory: $([math]::Round($proc.WorkingSet64/1MB,2)) MB" -ForegroundColor Gray

        } catch {
            Write-Host "Could not get detailed process info (admin required)" -ForegroundColor Red
        }
    }

    # Check for hidden PowerShell processes
    Write-Host "`n=== Checking for Hidden PowerShell Processes ===" -ForegroundColor Cyan

    # Method 1: WMI query for all processes
    try {
        $allProcs = Get-WmiObject Win32_Process -ErrorAction Stop | Where-Object {
            $_.Name -match "powershell|PowerShell|pwsh" -or
            $_.CommandLine -match "powershell"
        }

        $visiblePids = $psProcesses.Id
        $hiddenProcs = $allProcs | Where-Object { $_.ProcessId -notin $visiblePids }

        if ($hiddenProcs) {
            Write-Host "[!] HIDDEN POWERSHELL PROCESSES FOUND!" -ForegroundColor Red
            foreach ($proc in $hiddenProcs) {
                Write-Host "  PID: $($proc.ProcessId)" -ForegroundColor Yellow
                Write-Host "  Name: $($proc.Name)" -ForegroundColor Yellow
                if ($proc.CommandLine) {
                    Write-Host "  Command: $($proc.CommandLine)" -ForegroundColor Magenta
                }
            }
        } else {
            Write-Host "[+] No hidden PowerShell processes found" -ForegroundColor Green
        }

    } catch {
        Write-Host "[-] Could not check for hidden processes: $_" -ForegroundColor Red
    }
}

