function Detect-AMSIBypass {
    Write-Host "`n=== AMSI Bypass Detection ===" -ForegroundColor Cyan

    # Check current AMSI context
    try {
        # Method 1: Check if AMSI is initialized
        $amsiContext = [Ref].Assembly.GetType('System.Management.Automation.AmsiUtils').GetField('amsiContext', 'NonPublic,Static').GetValue($null)
        if ($amsiContext -eq 0) {
