        }
    } catch {
        Write-Host "[-] Could not check AMSI context: $_" -ForegroundColor Red
    }

    # Check for common AMSI bypass strings in history
    $bypassPatterns = @(
        "amsiInitFailed",
        "AmsiUtils",
        "amsiContext",
        '\[Ref\].Assembly.GetType',
        "System.Management.Automation.AmsiUtils",
        "amsi.dll",
        "AmsiScanBuffer"
    )

    $historyPath = (Get-PSReadlineOption).HistorySavePath
    if (Test-Path $historyPath) {
        $history = Get-Content $historyPath -ErrorAction SilentlyContinue
        if ($history) {
            foreach ($pattern in $bypassPatterns) {
                $matches = $history | Select-String -Pattern $pattern
                if ($matches) {
                    Write-Host "[!] AMSI bypass pattern '$pattern' found in history" -ForegroundColor Red
                    $matches | Select-Object -First 3 | ForEach-Object {
                        Write-Host "  Command: $($_.Line)" -ForegroundColor Magenta
                    }
                }
            }
        }
    }

    # Check event logs for AMSI bypass attempts
    Write-Host "`nChecking Event Logs for AMSI Bypass..." -ForegroundColor Yellow

    try {
        $events = Get-WinEvent -FilterHashtable @{
            LogName = 'Microsoft-Windows-PowerShell/Operational'
            Id = 4104  # Script Block logging
        } -MaxEvents 1000 -ErrorAction Stop | 
        Where-Object { $_.Properties.Count -ge 3 -and $_.Properties[2].Value -match 'amsi' }

        if ($events) {
            Write-Host "[!] AMSI-related events found in logs: $($events.Count)" -ForegroundColor Red
            $events | Select-Object -First 3 | ForEach-Object {
                Write-Host "  Time: $($_.TimeCreated)" -ForegroundColor Gray
                Write-Host "  Content: $($_.Properties[2].Value.Substring(0, 200))..." -ForegroundColor Magenta
            }
        }
    } catch {
        Write-Host "[-] Could not check event logs: $_" -ForegroundColor Red
    }

    # Check for AMSI DLL hijacking
    Write-Host "`nChecking for AMSI DLL hijacking..." -ForegroundColor Yellow

    $amsiDllPath = "C:\Windows\System32\amsi.dll"
    if (Test-Path $amsiDllPath) {
        $dllHash = (Get-FileHash $amsiDllPath -Algorithm SHA256 -ErrorAction SilentlyContinue).Hash
        $knownGoodHash = "KNOWN_GOOD_HASH_HERE"  # You would need to fill this in

        if ($dllHash) {
            Write-Host "AMSI.dll SHA256: $dllHash" -ForegroundColor Gray

            # Check digital signature
            $sig = Get-AuthenticodeSignature -FilePath $amsiDllPath -ErrorAction SilentlyContinue
            if ($sig.Status -ne "Valid") {
                Write-Host "[!] AMSI.dll has invalid or no digital signature!" -ForegroundColor Red
            } else {
                Write-Host "[+] AMSI.dll signature is valid" -ForegroundColor Green
            }
        }
    }
}

