function Detect-PowerShellObfuscation {
    param(
        [string]$HistoryPath = (Get-PSReadlineOption).HistorySavePath
    )

    Write-Host "`n=== PowerShell Obfuscation Detection ===" -ForegroundColor Cyan

    if (-not (Test-Path $HistoryPath)) {
        Write-Host "[-] History file not found" -ForegroundColor Red
        return
    }

    $history = Get-Content $HistoryPath -ErrorAction SilentlyContinue
    if (-not $history) {
        Write-Host "[-] No history content" -ForegroundColor Red
        return
    }

    # Obfuscation patterns
    $obfuscationPatterns = @(
        # String concatenation
        @{Pattern = '["'']\s*\+\s*["'']'; Description = 'String concatenation'},

        # Base64 encoded commands
        @{Pattern = '-EncodedCommand\s+[A-Za-z0-9+/]+={0,2}'; Description = 'Base64 encoded command'},

        # Character codes
        @{Pattern = '\[char\](0x[0-9a-fA-F]+|\d+)'; Description = 'Character code obfuscation'},

        # Environment variable obfuscation
        @{Pattern = '\$env:[^ ]+'; Description = 'Environment variable usage'},

        # IEX variations
        @{Pattern = 'iex|IEX|Invoke-Expression|\.\(|& \('; Description = 'Invoke-Expression usage'},

        # Download cradles
        @{Pattern = 'DownloadString|DownloadData|DownloadFile|WebClient|Net.WebClient'; Description = 'Web download'},

        # Reflection
        @{Pattern = '\[Reflection.Assembly\]|GetType|GetMethod|Invoke'; Description = 'Reflection usage'},

        # PowerShell v2 downgrade
        @{Pattern = '-Version\s+2|-v\s+2'; Description = 'PowerShell v2 downgrade'},

        # No profile
        @{Pattern = '-NoProfile'; Description = 'No profile flag'},

        # Bypass execution policy
        @{Pattern = '-ExecutionPolicy\s+Bypass|-ep\s+Bypass'; Description = 'Execution policy bypass'},

        # Window style hidden
        @{Pattern = '-WindowStyle\s+Hidden|-w\s+Hidden'; Description = 'Hidden window'},

        # Non-interactive
        @{Pattern = '-NonInteractive'; Description = 'Non-interactive flag'}
    )

    $suspiciousCommands = @()

    foreach ($command in $history) {
        if ($command.Trim().Length -gt 0) {
            $obfuscationFlags = @()

            foreach ($pattern in $obfuscationPatterns) {
                if ($command -match $pattern.Pattern) {
                    $obfuscationFlags += $pattern.Description
                }
            }

            if ($obfuscationFlags.Count -gt 0) {
                $suspiciousCommands += [PSCustomObject]@{
                    Command = $command
                    ObfuscationTechniques = $obfuscationFlags -join ', '
                    FlagCount = $obfuscationFlags.Count
                }
            }
        }
    }

    if ($suspiciousCommands) {
        Write-Host "[!] Found $($suspiciousCommands.Count) potentially obfuscated commands" -ForegroundColor Red

        $suspiciousCommands | Sort-Object FlagCount -Descending | Select-Object -First 10 | ForEach-Object {
            Write-Host "`nCommand: $($_.Command)" -ForegroundColor Yellow
            Write-Host "Techniques: $($_.ObfuscationTechniques)" -ForegroundColor Magenta
            Write-Host "Flag Count: $($_.FlagCount)" -ForegroundColor Gray
        }
    } else {
        Write-Host "[+] No obfuscation detected in history" -ForegroundColor Green
    }

    # Additional checks
    Write-Host "`n=== Additional Checks ===" -ForegroundColor Cyan

    # Check for extremely long lines (potential encoded commands)
    $longCommands = $history | Where-Object { $_.Length -gt 1000 }
    if ($longCommands) {
        Write-Host "[!] Found $($longCommands.Count) commands longer than 1000 characters" -ForegroundColor Red
        $longCommands | Select-Object -First 3 | ForEach-Object {
            Write-Host "  Length: $($_.Length) chars" -ForegroundColor Gray
            Write-Host "  Preview: $($_.Substring(0, 100))..." -ForegroundColor Gray
        }
    }

    # Check for compressed/encoded strings
    $gzipCommands = $history | Where-Object { $_ -match 'GZipStream|DeflateStream|System.IO.Compression' }
    if ($gzipCommands) {
        Write-Host "[!] Found $($gzipCommands.Count) commands using compression" -ForegroundColor Red
    }

    # Check for XOR operations
    $xorCommands = $history | Where-Object { $_ -match '\^|\s+xor\s+| -bxor ' }
    if ($xorCommands) {
        Write-Host "[!] Found $($xorCommands.Count) commands using XOR operations" -ForegroundColor Red
    }
}

