function Invoke-PowerShellForensics {
    param(
        [string]$OutputDir = "C:\Temp\PowerShell_Forensics_$(Get-Date -Format 'yyyyMMdd_HHmmss')",
        [switch]$CollectAll,
        [switch]$SkipMemory,
        [int]$EventLogDays = 30
    )

    # Create output directory
    New-Item -ItemType Directory -Path $OutputDir -Force | Out-Null
    Write-Host "Output directory: $OutputDir" -ForegroundColor Green

    # Start transcript
    $transcriptPath = Join-Path $OutputDir "Collection_Transcript.txt"
    Start-Transcript -Path $transcriptPath -Append

    try {
        Write-Host "`n=== PowerShell Forensics Collection Started ===" -ForegroundColor Cyan
        Write-Host "Time: $(Get-Date)" -ForegroundColor Yellow

        # 1. System Information
        Write-Host "`n[1] Collecting System Information..." -ForegroundColor Green
        $systemInfo = @{
            ComputerName = $env:COMPUTERNAME
            UserName = $env:USERNAME
            Domain = $env:USERDOMAIN
            OSVersion = [System.Environment]::OSVersion.VersionString
            PowerShellVersion = $PSVersionTable.PSVersion
            CLRVersion = $PSVersionTable.CLRVersion
            BuildVersion = $PSVersionTable.BuildVersion
            WSManStackVersion = $PSVersionTable.WSManStackVersion
        }

        $systemInfo | ConvertTo-Json | Out-File (Join-Path $OutputDir "System_Info.json")

        # 2. Collect PowerShell History from All Users
        Write-Host "`n[2] Collecting PowerShell History..." -ForegroundColor Green
        $historyResults = @()

        $userProfiles = Get-ChildItem -Path "C:\Users" -Directory -ErrorAction SilentlyContinue
        foreach ($profile in $userProfiles) {
            $historyPath = Join-Path $profile.FullName "AppData\Roaming\Microsoft\Windows\PowerShell\PSReadLine\ConsoleHost_history.txt"

            if (Test-Path $historyPath) {
                $historyContent = Get-Content $historyPath -ErrorAction SilentlyContinue
                if ($historyContent) {
                    $historyResults += [PSCustomObject]@{
                        User = $profile.Name
                        HistoryPath = $historyPath
                        LineCount = $historyContent.Count
                        Last10Commands = $historyContent | Select-Object -Last 10
                        AllCommands = $historyContent
                    }

                    # Save individual history file
                    $userHistoryFile = Join-Path $OutputDir "History_$($profile.Name).txt"
                    $historyContent | Out-File $userHistoryFile
                }
            }
        }

        $historyResults | ConvertTo-Json -Depth 3 | Out-File (Join-Path $OutputDir "All_Users_History.json")

        # 3. Collect Event Logs
        Write-Host "`n[3] Collecting Event Logs..." -ForegroundColor Green
        $logStartTime = (Get-Date).AddDays(-$EventLogDays)

        $eventLogs = @(
            'Microsoft-Windows-PowerShell/Operational',
            'Windows PowerShell',
            'System',
            'Security',
            'Application'
        )

        foreach ($logName in $eventLogs) {
            Write-Host "  Collecting: $logName" -ForegroundColor Gray

            try {
                $events = Get-WinEvent -LogName $logName -MaxEvents 1000 -ErrorAction Stop |
                         Where-Object { $_.TimeCreated -gt $logStartTime } |
                         Select-Object TimeCreated, Id, LevelDisplayName, ProviderName, Message

                if ($events) {
                    $logFile = Join-Path $OutputDir "EventLog_$($logName.Replace('/', '_')).csv"
                    $events | Export-Csv -Path $logFile -NoTypeInformation
                }
            } catch {
                Write-Host "  Could not collect $logName: $_" -ForegroundColor Red
            }
        }

        # 4. Collect PowerShell Configuration
        Write-Host "`n[4] Collecting PowerShell Configuration..." -ForegroundColor Green

        # Execution policies
        $executionPolicies = Get-ExecutionPolicy -List
        $executionPolicies | Export-Csv (Join-Path $OutputDir "Execution_Policies.csv") -NoTypeInformation

        # Module paths
        $env:PSModulePath -split ';' | Out-File (Join-Path $OutputDir "Module_Paths.txt")

        # Loaded modules
        Get-Module | Select-Object Name, Version, Path | Export-Csv (Join-Path $OutputDir "Loaded_Modules.csv") -NoTypeInformation

        # 5. Collect Process Information
        if (-not $SkipMemory) {
            Write-Host "`n[5] Collecting Process Information..." -ForegroundColor Green

            # PowerShell processes
            $psProcesses = Get-Process | Where-Object { 
                $_.ProcessName -match "powershell|pwsh|PowerShell" 
            } | Select-Object Id, ProcessName, StartTime, CPU, WorkingSet64, Path

            if ($psProcesses) {
                $psProcesses | Export-Csv (Join-Path $OutputDir "PowerShell_Processes.csv") -NoTypeInformation

                # Try to get command line (requires admin)
                foreach ($proc in $psProcesses) {
                    try {
                        $wmiProc = Get-WmiObject Win32_Process -Filter "ProcessId = $($proc.Id)" -ErrorAction Stop
                        if ($wmiProc.CommandLine) {
                            Add-Content -Path (Join-Path $OutputDir "Process_CommandLines.txt") -Value "PID $($proc.Id): $($wmiProc.CommandLine)"
                        }
                    } catch {}
                }
            }
        }

        # 6. Collect Registry Settings
        Write-Host "`n[6] Collecting Registry Settings..." -ForegroundColor Green

        $registryPaths = @(
            "HKLM:\SOFTWARE\Microsoft\PowerShell",
            "HKLM:\SOFTWARE\Policies\Microsoft\Windows\PowerShell",
            "HKCU:\SOFTWARE\Microsoft\PowerShell",
            "HKCU:\SOFTWARE\Microsoft\WindowsPowerShell"
        )

        foreach ($regPath in $registryPaths) {
            if (Test-Path $regPath) {
                $regFile = Join-Path $OutputDir "Registry_$($regPath.Replace(':', '').Replace('\', '_')).txt"
                reg export $regPath.Replace(':', '') $regFile 2>$null
            }
        }

        # 7. Check for Defense Evasion
        Write-Host "`n[7] Checking for Defense Evasion..." -ForegroundColor Green

        $defenseChecks = @{
            AMSI_Enabled = $null
            ScriptBlockLogging = $null
            ModuleLogging = $null
            Transcription = $null
        }

        # Check AMSI
        try {
            $amsiContext = [Ref].Assembly.GetType('System.Management.Automation.AmsiUtils').GetField('amsiContext', 'NonPublic,Static').GetValue($null)
            $defenseChecks.AMSI_Enabled = $amsiContext -ne 0
        } catch {
            $defenseChecks.AMSI_Enabled = "Error"
        }

        # Check logging settings
        $scriptLogging = Get-ItemProperty -Path "HKLM:\SOFTWARE\Policies\Microsoft\Windows\PowerShell\ScriptBlockLogging" -Name "EnableScriptBlockLogging" -ErrorAction SilentlyContinue
        $defenseChecks.ScriptBlockLogging = if ($scriptLogging -and $scriptLogging.EnableScriptBlockLogging -eq 1) { $true } else { $false }

        $defenseChecks | ConvertTo-Json | Out-File (Join-Path $OutputDir "Defense_Checks.json")

        # 8. Generate Summary Report
        Write-Host "`n[8] Generating Summary Report..." -ForegroundColor Green

        $summary = @"
=== PowerShell Forensics Summary ===
Collection Time: $(Get-Date)
Computer: $env:COMPUTERNAME
User: $env:USERNAME

=== Statistics ===
Users with PowerShell History: $(($historyResults | Where-Object { $_.LineCount -gt 0 }).Count)
Total History Commands: $(($historyResults.AllCommands | Measure-Object).Count)
PowerShell Processes: $(if ($psProcesses) { $psProcesses.Count } else { "N/A" })

=== Defense Status ===
AMSI Enabled: $($defenseChecks.AMSI_Enabled)
Script Block Logging: $($defenseChecks.ScriptBlockLogging)
Module Logging: $($defenseChecks.ModuleLogging)
Transcription: $($defenseChecks.Transcription)

=== Suspicious Findings ===
$(
    $suspicious = @()
    if ($historyResults) {
        $suspiciousCommands = $historyResults.AllCommands | Where-Object { 
            $_ -match 'IEX|DownloadString|bypass|amsi|mimikatz|Add-MpPreference'
        }
        if ($suspiciousCommands) {
            $suspicious += "Found $($suspiciousCommands.Count) suspicious commands in history"
        }
    }
    $suspicious -join "`n"
)

=== Files Collected ===
$(Get-ChildItem $OutputDir | Select-Object Name, Length | Format-List | Out-String)

=== Collection Complete ===
"@

        $summary | Out-File (Join-Path $OutputDir "SUMMARY_REPORT.txt")

        Write-Host "`n=== Collection Complete ===" -ForegroundColor Cyan
        Write-Host "Output saved to: $OutputDir" -ForegroundColor Green
        Write-Host "Summary saved to: $(Join-Path $OutputDir "SUMMARY_REPORT.txt")" -ForegroundColor Green

        # Show quick summary
        Write-Host "`nQuick Summary:" -ForegroundColor Yellow
        Get-Content (Join-Path $OutputDir "SUMMARY_REPORT.txt") | Select-String "Statistics:", "Suspicious Findings:" -Context 0,3

    } catch {
        Write-Host "Error during collection: $_" -ForegroundColor Red
    } finally {
        Stop-Transcript
    }

    return $OutputDir
}

# Run complete forensics collection
# Invoke-PowerShellForensics -OutputDir "C:\Temp\PS_Forensics" -EventLogDays 7
